import React from 'react';

export default function ProductCard({ product, onEdit, onDelete }) {
  return (
    <div style={{border:'1px solid #ddd', padding:12, borderRadius:8, background:'#fff'}}>
      {product.image && <img src={product.image} alt={product.title} style={{width:'100%', height:150, objectFit:'cover', borderRadius:6}} />}
      <h3>{product.title}</h3>
      <p style={{marginTop:4}}>{product.description}</p>
      <p><strong>Price:</strong> â‚¹{product.price}</p>
      <p><strong>In stock:</strong> {product.inStock ? 'Yes' : 'No'}</p>
      <div style={{marginTop:8, display:'flex', gap:8}}>
        <button onClick={onEdit}>Edit</button>
        <button onClick={onDelete} style={{background:'#e84'}}>Delete</button>
      </div>
    </div>
  );
}
