import React, { useState, useEffect } from 'react';

export default function ProductModal({ product, onClose, onSave }) {
  const [form, setForm] = useState({
    title: '',
    description: '',
    price: 0,
    image: '',
    inStock: true
  });

  useEffect(() => {
    if (product) {
      setForm({
        title: product.title || '',
        description: product.description || '',
        price: product.price || 0,
        image: product.image || '',
        inStock: product.inStock ?? true
      });
    } else {
      setForm({ title: '', description: '', price: 0, image: '', inStock: true });
    }
  }, [product]);

  function change(e) {
    const { name, value, type, checked } = e.target;
    setForm(prev => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  }

  function submit(e) {
    e.preventDefault();
    const payload = { ...form, price: Number(form.price) || 0 };
    onSave(payload);
  }

  return (
    <div style={overlayStyle}>
      <div style={modalStyle}>
        <h3>{product ? 'Edit Product' : 'Add Product'}</h3>
        <form onSubmit={submit}>
          <div><input name="title" value={form.title} onChange={change} placeholder="Title" required /></div>
          <div><textarea name="description" value={form.description} onChange={change} placeholder="Description" /></div>
          <div><input name="price" type="number" value={form.price} onChange={change} step="0.01" /></div>
          <div><input name="image" value={form.image} onChange={change} placeholder="Image URL" /></div>
          <div><label><input name="inStock" type="checkbox" checked={form.inStock} onChange={change} /> In stock</label></div>
          <div style={{marginTop:8}}>
            <button type="submit">Save</button>
            <button type="button" onClick={onClose} style={{marginLeft:8}}>Cancel</button>
          </div>
        </form>
      </div>
    </div>
  );
}

const overlayStyle = {
  position:'fixed', top:0, left:0, right:0, bottom:0, background:'rgba(0,0,0,0.4)', display:'flex', alignItems:'center', justifyContent:'center', zIndex:9999
};
const modalStyle = {
  background:'#fff', padding:16, borderRadius:8, width:600, maxWidth:'95%'
};
